glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
#endif
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Properties {
  vec4 colors[4];
  vec4 surfaceColors[4];
  vec4 heights[4];
  vec2 iResolution;
  float skewAngle;
  float waveType;
};
vec2 tranPt(vec2 uv,float angle,vec2 center){
  float c = cos(angle);
  float s = sin(angle);
  float Ox = center.x;
  float Oy = center.y;
  mat3 m = mat3(
        c,0.0-s,(1.0-c)*Ox+s*Oy,
        s,c,(1.0-c)*Oy-s*Ox,
        0.0,0.0,1.0
  );
  vec3 ret = m*vec3(uv.x,uv.y,0.0);
  return ret.xy;
}
float drawWater(vec2 uv,float angle,float _height,int arrSize,int curIdx)
{
    float ratio = iResolution.y/iResolution.x;
    angle = mod(angle, 2.0*3.1415);
    bool inverted = angle > 0.5*3.1415 && angle < 1.5*3.1415;
    vec2 center = inverted ? vec2(0.5, _height) : vec2(0.5, 1.0 - _height);
    float effectiveAngle = inverted ? angle + 3.1415 : angle;
    effectiveAngle = mod(effectiveAngle, 2.0*3.1415);
    float cosAngle = abs(cos(effectiveAngle));
    float sinAngle = abs(sin(effectiveAngle));
    float ellipseRadiusX = cosAngle < 0.01 ? 10.0 : 0.5 / cosAngle;
    float ellipseRadiusY = 0.20 * cosAngle;
    vec2 adjustedUV = uv;
    adjustedUV.y *= ratio;
    adjustedUV -= vec2(center.x, center.y * ratio);
    vec2 uv1 = tranPt(adjustedUV, effectiveAngle, vec2(0.0));
    float normX = uv1.x / ellipseRadiusX;
    float normY = uv1.y / ellipseRadiusY;
    float distToCenter = normX * normX + normY * normY;
    bool inEllipse = (distToCenter <= 1.0);
    bool isTopLayer = (curIdx == arrSize - 1);
    float waterLineY = 0.0;
    float alpha = 0.0;
    if (!inverted && uv1.y > waterLineY && !inEllipse) alpha = 1.0;
    if (inverted && uv1.y < waterLineY && !inEllipse) alpha = 1.0;
    if (isTopLayer) {
        if ((!inverted && (uv1.y > waterLineY || distToCenter <= (1.0 ))) ||
            (inverted && (uv1.y < waterLineY || distToCenter <= (1.0 )))) {
            alpha = 1.0;
            if(distToCenter <= (1.0)) {
                alpha = 0.9;
            }
        } else {
            alpha = 0.0;
        }
    }
    return alpha;
}
void main () {
  vec4 ret = vec4(1.0,1.0,1.0,1.0);
  ret *= texture(texture, v_uv0);
  if(ret.a == 0.0){
    discard;
  }
  float angle = mod(skewAngle,360.0) * 3.1415 / 180.0;
  vec2 uv = vec2(v_uv0);
  float a = 0.0;
  float _height = 0.0;
  int size = 0;
  for(int i=0;i<4;i++){
    if(heights[i].x<0.001){
      continue;
    }
    size++;
  }
  for(int i=0;i<4;i++){
    if(heights[i].x<0.001){
      continue;
    }
    _height+=heights[i].x;
    a += drawWater(uv,angle,_height,size,i);
    if(a>0.0){
      if(a == 0.9) {
          ret *= surfaceColors[i];
      } else {
          vec4 color = colors[i];
          for(int idx = 0; idx < 4; idx++){
            if(i==idx){
              color = colors[idx];
            }
          }
          ret *= a * color;
      }
      break;
    }
  }
  if(a<0.001){
    discard;
  }
  gl_FragColor = ret;
}

